#version 100

uniform 	vec4 hlslcc_mtx4x4unity_ObjectToWorld[4];
uniform 	vec4 hlslcc_mtx4x4unity_MatrixVP[4];
uniform 	vec4 _MainTex_ST;
uniform 	vec4 _Decor_ST;
uniform 	vec4 _Mask_ST;
uniform 	vec4 _Muzzle;
attribute highp vec4 in_POSITION0;
attribute highp vec2 in_TEXCOORD0;
varying highp vec2 vs_TEXCOORD0;
varying highp vec2 vs_TEXCOORD2;
varying highp vec2 vs_TEXCOORD3;
varying highp float vs_TEXCOORD4;
vec4 u_xlat0;
vec4 u_xlat1;
void main()
{
    u_xlat0 = in_POSITION0.yyyy * hlslcc_mtx4x4unity_ObjectToWorld[1];
    u_xlat0 = hlslcc_mtx4x4unity_ObjectToWorld[0] * in_POSITION0.xxxx + u_xlat0;
    u_xlat0 = hlslcc_mtx4x4unity_ObjectToWorld[2] * in_POSITION0.zzzz + u_xlat0;
    u_xlat1 = u_xlat0 + hlslcc_mtx4x4unity_ObjectToWorld[3];
    u_xlat0.xyz = hlslcc_mtx4x4unity_ObjectToWorld[3].xyz * in_POSITION0.www + u_xlat0.xyz;
    u_xlat0.xyz = u_xlat0.xyz + (-_Muzzle.xyz);
    u_xlat0.x = dot(u_xlat0.xyz, u_xlat0.xyz);
    vs_TEXCOORD4 = sqrt(u_xlat0.x);
    u_xlat0 = u_xlat1.yyyy * hlslcc_mtx4x4unity_MatrixVP[1];
    u_xlat0 = hlslcc_mtx4x4unity_MatrixVP[0] * u_xlat1.xxxx + u_xlat0;
    u_xlat0 = hlslcc_mtx4x4unity_MatrixVP[2] * u_xlat1.zzzz + u_xlat0;
    gl_Position = hlslcc_mtx4x4unity_MatrixVP[3] * u_xlat1.wwww + u_xlat0;
    vs_TEXCOORD0.xy = in_TEXCOORD0.xy * _MainTex_ST.xy + _MainTex_ST.zw;
    vs_TEXCOORD2.xy = in_TEXCOORD0.xy * _Decor_ST.xy + _Decor_ST.zw;
    vs_TEXCOORD3.xy = in_TEXCOORD0.xy * _Mask_ST.xy + _Mask_ST.zw;
    return;
}


#version 100

#ifdef GL_FRAGMENT_PRECISION_HIGH
    precision highp float;
#else
    precision mediump float;
#endif
precision highp int;
uniform 	mediump vec4 _GlowColor;
uniform 	mediump float _MulStrength;
uniform 	mediump float _Opacity;
uniform 	vec4 _Glow;
uniform 	mediump float _MulOrBlend;
uniform lowp sampler2D _MainTex;
uniform lowp sampler2D _Decor;
uniform lowp sampler2D _Mask;
varying highp vec2 vs_TEXCOORD0;
varying highp vec2 vs_TEXCOORD2;
varying highp vec2 vs_TEXCOORD3;
varying highp float vs_TEXCOORD4;
#define SV_Target0 gl_FragData[0]
vec3 u_xlat0;
lowp vec4 u_xlat10_0;
lowp float u_xlat10_1;
mediump vec3 u_xlat16_2;
mediump vec3 u_xlat16_3;
vec2 u_xlat4;
mediump float u_xlat16_14;
void main()
{
    u_xlat10_0 = texture2D(_Decor, vs_TEXCOORD2.xy);
    u_xlat10_1 = texture2D(_Mask, vs_TEXCOORD3.xy).x;
    u_xlat16_2.xyz = u_xlat10_0.xyz * vec3(u_xlat10_1);
    u_xlat16_14 = u_xlat10_1 * _Opacity;
    u_xlat16_14 = u_xlat10_0.w * u_xlat16_14;
    u_xlat16_3.xyz = u_xlat16_2.xyz * vec3(_MulStrength);
    u_xlat10_0.xyz = texture2D(_MainTex, vs_TEXCOORD0.xy).xyz;
    u_xlat16_2.xyz = u_xlat16_2.xyz * u_xlat10_0.xyz;
    u_xlat16_2.xyz = u_xlat16_2.xyz * vec3(_MulStrength) + (-u_xlat16_3.xyz);
    u_xlat16_2.xyz = vec3(_MulOrBlend) * u_xlat16_2.xyz + u_xlat16_3.xyz;
    u_xlat16_2.xyz = (-u_xlat10_0.xyz) + u_xlat16_2.xyz;
    u_xlat16_2.xyz = vec3(u_xlat16_14) * u_xlat16_2.xyz + u_xlat10_0.xyz;
    u_xlat0.x = vs_TEXCOORD4 + (-_Glow.x);
    u_xlat4.xy = (-_Glow.xz) + _Glow.yw;
    u_xlat0.x = u_xlat0.x / u_xlat4.x;
    u_xlat0.x = clamp(u_xlat0.x, 0.0, 1.0);
    u_xlat0.x = u_xlat0.x * u_xlat4.y + _Glow.z;
    u_xlat0.xyz = u_xlat0.xxx * _GlowColor.xyz + u_xlat16_2.xyz;
    SV_Target0.xyz = u_xlat0.xyz;
    SV_Target0.w = 1.0;
    return;
}
